}

#drawer .container li.saved-color {
  opacity: 0.95;
  position: relative;
  cursor: pointer;
  height: 100px;
  width: 100%;
  margin-bottom: 5px;
}

#drawer .container li.saved-color:hover {
  opacity: 1.0;
}

#drawer .container li.saved-color span {
  display: block;
  float: left;
}

#drawer .container li.saved-color button.btn.destroy {
  float: none;
  position: absolute;
  background: #051924;
  opacity: 0.8;
  padding: 5px;
}

#header {
  *zoom: 1;
  position: absolute;
  bottom: 100%;
  width: 100%;
}

#header:before,
