  z-index: 10;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  overflow: hidden;
}

#colors .swatch .color {
  -webkit-transition: -webkit-transform 0.2s ease;
  -moz-transition: -webkit-transform 0.2s ease;
  -o-transition: -webkit-transform 0.2s ease;
  transition: -webkit-transform 0.2s ease;
  -webkit-transition: -moz-transform 0.2s ease;
  -moz-transition: -moz-transform 0.2s ease;
  -o-transition: -moz-transform 0.2s ease;
  transition: -moz-transform 0.2s ease;
  -webkit-transition: -ms-transform 0.2s ease;
  -moz-transition: -ms-transform 0.2s ease;
  -o-transition: -ms-transform 0.2s ease;
  transition: -ms-transform 0.2s ease;
  -webkit-transition: -o-transform 0.2s ease;
  -moz-transition: -o-transform 0.2s ease;
  -o-transition: -o-transform 0.2s ease;
  transition: -o-transform 0.2s ease;
  -webkit-transition: transform 0.2s ease;
  -moz-transition: transform 0.2s ease;
  -o-transition: transform 0.2s ease;
  transition: transform 0.2s ease;
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  z-index: 10;
}

#colors .swatch .meta-controls {
  position: absolute;
  bottom: 40px;
  text-align: center;
  width: 100%;
}

#colors .swatch .info {
  position: absolute;
  top: 50%;
  left: 50%;
  margin: 0 auto;
  padding: 20px 0;
  width: 110px;
  border-radius: 3px;
  -webkit-transform: translate3d(-50%, -50%, 0);
  -moz-transform: translate3d(-50%, -50%, 0);
  -o-transform: translate3d(-50%, -50%, 0);
  transform: translate3d(-50%, -50%, 0);
  background: rgba(0, 0, 0, 0.5);
  text-align: center;
}

#colors .swatch .info h2 {
  font-size: 14px;
  line-height: 30px;
  margin: 0;
  padding: 0;
}

#colors .swatch .info small {
  font-size: 10px;
}

#colors .swatch .info .controls {
  line-height: 20px;
}

#colors .swatch .details {
  position: absolute;
  bottom: 0;
  width: 100%;
  padding: 50px 0;
  min-width: 200px;
  text-align: center;
}

#colors .swatch.light .color .btn {
  color: rgba(0, 0, 0, 0.5);
}

#colors .swatch.light .color .btn:hover {
  color: rgba(0, 0, 0, 0.9);
}

#colors .swatch.show-details .color {
  -webkit-transform: translate3d(0, -260px, 0);
  -moz-transform: translate3d(0, -260px, 0);
  -o-transform: translate3d(0, -260px, 0);
  transform: translate3d(0, -260px, 0);
}

#edit {
  right: 0;
  -webkit-transition: left .2s ease;
  -moz-transition: left .2s ease;
  -o-transition: left .2s ease;
  transition: left .2s ease;
}

#constraints {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow-y: auto;
}

#constraints div {
  width: 100%;
  height: 100%;
  padding-bottom: 1000px;
}

/* ==========================================================================
   Widgets
   ========================================================================== */
.color-input {
  height: 80px;
  line-height: 80px;
}

.number-widget {
  position: relative;
  display: inline-block;
  height: 80px;
  line-height: 80px;
}

.number-widget [type="text"] {
  display: inline-block;
  width: 40px;
  line-height: 20px;
  text-align: center;
  color: white;
  outline: 0;
  border: none;
  background: transparent;
}

.number-widget .stepper {
  display: block;
  position: absolute;
  left: 0;
  width: 40px;
  padding: 0;
  margin: 0;
  line-height: 30px;
  height: 30px;
  text-align: center;
}

.number-widget .stepper.up {
  top: 0;
}

.number-widget .stepper.down {
  bottom: 0;
}
